import { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function AvgTimeChart({ predictions }) {
  const chartRef = useRef(null);

  // This useEffect hook runs whenever the "predictions" prop changes.
  useEffect(() => {
    // Destroy the previous chart instance, if any.
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    const data = predictions.reduce((acc, item) => {
      const date = new Date(item.date).toLocaleDateString('en-US');
    
      const countByDate = acc[date] || {
        //XSS: 0,
        //DDoS: 0,
        //BENIGN: 0,
        timeCount: 0,
        date,
        count: 0, // keep track of the number of predictions for averaging
      };
    
      countByDate[item.prediction] += 1;
      countByDate.timeCount += item.timeCount;
      countByDate.count += 1;
      acc[date] = countByDate;
      return acc;
    }, {});
     
  
    // Define the chart data and options.
    const chartData = {
      labels: Object.keys(data),
      datasets: [
        {
          label: 'Average Time Count',
          data: Object.values(data).map((prediction) => {
            const averageTimeCount = prediction.count !== 0 ? prediction.timeCount / prediction.count : 0;
            return averageTimeCount.toFixed(2); // round the average to 2 decimal places
          }),
          backgroundColor: 'orange',
        },
      ],
    };

    const chartConfig = {
      type: 'bar',
      data: chartData,
      options: {
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: 'Number of XSS and DDoS attacks by weekday'
          }
        },
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };

    // Get the canvas context and create a new chart instance.
    const ctx = document.getElementById('AvgTimeChart').getContext('2d');
  
    chartRef.current = new Chart(ctx, chartConfig);
  }, [predictions]); // The "predictions" prop is the dependency of this useEffect hook.

  return <canvas id="AvgTimeChart"></canvas>;
}

export default AvgTimeChart;
