import { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function LineChart({ predictions }) {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    // Calculate the average prediction time for each day
    const predictionTimes = {};
    const predictionCounts = {};
    predictions.forEach(prediction => {
      const date = new Date(prediction.date).toLocaleDateString('en-US');
      const time = prediction.time; // Assuming prediction.time is a valid time value

      if (predictionTimes[date]) {
        predictionTimes[date] += time;
        predictionCounts[date] += 1;
      } else {
        predictionTimes[date] = time;
        predictionCounts[date] = 1;
      }
    });

    const averageTimes = {};
    for (const date in predictionTimes) {
      averageTimes[date] = predictionTimes[date] / predictionCounts[date];
    }

    const chartData = {
      labels: Object.keys(averageTimes),
      datasets: [
        {
          label: 'Average prediction time',
          data: Object.values(averageTimes),
          backgroundColor: 'green',
        },
      ],
    };

    const chartConfig = {
      type: 'bar',
      data: chartData,
      options: {
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: 'Average Prediction Time by Day'
          }
        },
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };

    const ctx = document.getElementById('LineChart').getContext('2d');
   
    chartRef.current = new Chart(ctx, chartConfig);
  }, [predictions]);

  return <canvas id="LineChart"></canvas>;
}

export default LineChart;
