import { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function ChartComponent({ predictions }) {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    const data = predictions.reduce((acc, item) => {
      const date = new Date(item.date).toLocaleDateString('en-US', {
        weekday: 'long',
      });
      const count = acc[date] ? acc[date][item.prediction] + 1 : 1;
      acc[date] = {
        [item.prediction]: count,
        date,
      };
      return acc;
    }, {});

    const chartData = {
      labels: Object.keys(data),
      datasets: [
        {
          label: 'Attacks',
          data: Object.values(data).map((d) => d['ATTACK'] || 0),
          backgroundColor: 'blue',
        },
        {
          label: 'Benign',
          data: Object.values(data).map((d) => d['BENIGN'] || 0),
          backgroundColor: 'green',
        },
      ],
    };

    const chartConfig = {
      type: 'bar',
      data: chartData,
      options: {
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };

    const ctx = document.getElementById('myChart').getContext('2d');
    if (chartRef.current) {
      chartRef.current.destroy();
    }
    chartRef.current = new Chart(ctx, chartConfig);
  }, [predictions]);

  return <canvas id="myChart"></canvas>;
}

export default ChartComponent;
