import { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';
import './Chart.css';

function BarChart({ predictions }) {
  const chartRef = useRef(null);

  
  // This useEffect hook runs whenever the "predictions" prop changes.
  useEffect(() => {
    // Destroy the previous chart instance, if any.
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    // Transform the "predictions" data into a format that can be used by the chart.
    const data = predictions.reduce((acc, item) => {
      const date = new Date(item.date).toLocaleDateString('en-US', {
        weekday: 'long',
      });
      const count = acc[date] ? acc[date][item.prediction] + 1 : 1;
      acc[date] = {
        [item.prediction]: count,
        date,
      };
      return acc;
    }, {});

    // Define the chart data and options.
    const chartData = {
      labels: Object.keys(data),
      datasets: [
        {
          label: 'Attacks',
          data: Object.values(data).map((d) => d['ATTACK'] || 0),
          backgroundColor: 'blue',
        },
        {
          label: 'Benign',
          data: Object.values(data).map((d) => d['BENIGN'] || 0),
          backgroundColor: 'green',
        },
      ],
    };

    const chartConfig = {
      type: 'bar',
      data: chartData,
      options: {
        maintainAspectRatio: false,
       // width: 200,
       // height: 400,
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };
    

    // Get the canvas context and create a new chart instance.
    const ctx = document.getElementById('myChart').getContext('2d');
    if (chartRef.current) {
      chartRef.current.destroy();
    }
    chartRef.current = new Chart(ctx, chartConfig);

  }, [predictions]); // The "predictions" prop is the dependency of this useEffect hook.

  return <canvas id="myChart" className="chart-canvas"></canvas>;
}

export default BarChart;
