import React, { useEffect, useState } from 'react';
import axios from 'axios';
import BarChart from './components/BarChart';
//import PieChart from './components/PieChart';

const baseURL = 'http://localhost:5000/api';

function App() {
  const [predictions, setPredictions] = useState([]);

  // This useEffect hook runs once, when the component is mounted.
  useEffect(() => {
    // Fetch the data from the API and update the state with it.
    axios.get(`${baseURL}/get`).then((res) => {
      setPredictions(res.data);
      //console.log(res.data);
    });
  }, []);

  return (
    <div>
      {/* Render the ChartComponent and pass the predictions state as a prop. */}
      <BarChart predictions={predictions} />
    {/*  <PieChart predictions={predictions} /> */}
    </div>
  );
}

export default App;
