import { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function BarChart({ predictions }) {
  const chartRef = useRef(null);

  // This useEffect hook runs whenever the "predictions" prop changes.
  useEffect(() => {
    // Destroy the previous chart instance, if any.
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    // Transform the "predictions" data into a format that can be used by the chart.
    const data = predictions.reduce((acc, item) => {
      const date = new Date(item.date).toLocaleDateString('en-US', {
        //weekday: 'long',
      });
      const count = acc[date] ? acc[date][item.prediction] + 1 : 1;
      acc[date] = {
        [item.prediction]: count,
        date,
      };
      return acc;
    }, {});

    // Calculate the total number of "ATTACK" and "BENIGN" predictions.
    let totalAttack = 0;
    let totalBenign = 0;
    Object.values(data).forEach((d) => {
      totalAttack += d['ATTACK'] || 0;
      totalBenign += d['BENIGN'] || 0;
    });

    // Define the chart data and options.
    const chartData = {
      labels: Object.keys(data),
      datasets: [
        {
          label: 'Total Attacks: ' + totalAttack,
          data: Object.values(data).map((d) => !d['DDoS'] || 0),
          backgroundColor: 'blue',
        },
        {
          label: 'Total Benign: ' + totalBenign,
          data: Object.values(data).map((d) => d['BENIGN'] || 0),
          backgroundColor: 'green',
        },
      ],
    };

    const chartConfig = {
      type: 'bar',
      data: chartData,
      options: {
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: 'Bar Chart'
          }
        },
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };

    // Get the canvas context and create a new chart instance.
    const ctx = document.getElementById('BarChart').getContext('2d');
    if (chartRef.current) {
      chartRef.current.destroy();
    }
    chartRef.current = new Chart(ctx, chartConfig);
  }, [predictions]); // The "predictions" prop is the dependency of this useEffect hook.

  return <canvas id="BarChart"></canvas>

}

export default BarChart;
