import { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function AvgTimeChart({ predictions }) {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    // Calculate the average prediction time for each day
    const timePrediction = {};
    const predictionCounts = {};
    predictions.forEach(prediction => {
      const date = new Date(prediction.date).toLocaleDateString('en-US');
      const time = prediction.time; 

      if (timePrediction[date]) {
        timePrediction[date] += time;
        predictionCounts[date] += 1;
      } else {
        timePrediction[date] = time;
        predictionCounts[date] = 0;
      }
    });

    const avgTime = {};
    for (const date in timePrediction) {
      console.log(date, timePrediction[date], predictionCounts[date])
      avgTime[date] = timePrediction[date] / predictionCounts[date];
    }

    const chartData = {
      labels: Object.keys(avgTime),
      datasets: [
        {
          label: 'Average prediction time',
          data: Object.values(avgTime),
          backgroundColor: 'orange',
        },
      ],
    };

    const chartConfig = {
      type: 'bar',
      data: chartData,
      options: {
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: 'Average prediction time by day'
          }
        },
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };

    const ctx = document.getElementById('AvgTimeChart').getContext('2d');
   
    chartRef.current = new Chart(ctx, chartConfig);
  }, [predictions]);

  return <canvas id="AvgTimeChart"></canvas>;
}

export default AvgTimeChart;
