import { useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function BenignLineChart({ predictions }) {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    // Count the number of XSS and DDoS attacks for each hour of the day
    const attackCounts = new Array(24).fill(0);
    predictions.forEach(prediction => {
      if (prediction.prediction === "BENIGN") {
        const date = new Date(prediction.date);
        const hour = date.getHours();
        attackCounts[hour] += 1;
      }
    });

    const chartData = {
      labels: Array.from({ length: 24 }, (_, i) => `${i}:00`),
      datasets: [
        {
          label: 'Number of benign',
          data: attackCounts,
          backgroundColor: 'green',
        },
      ],
    };

    const chartConfig = {
      type: 'line',
      data: chartData,
      options: {
        maintainAspectRatio: false,
        plugins: {
          title: {
            display: true,
            text: 'Numbers of benign by hour of the day'
          }
        },
        scales: {
          y: {
            beginAtZero: true,
          },
        },
      },
    };

    const ctx = document.getElementById('BenignLineChart').getContext('2d');
    if (chartRef.current) {
      chartRef.current.destroy();
    }
    chartRef.current = new Chart(ctx, chartConfig);
  }, [predictions]);

  return <canvas id="BenignLineChart"></canvas>;
}

export default BenignLineChart;
