import { useState, useEffect, useRef } from 'react';
import Chart from 'chart.js/auto';

function BarChart({ predictions }) {
  const chartRef = useRef(null);
  const [showWeekdayBars, setShowWeekdayBars] = useState(false);

  // This useEffect hook runs whenever the "predictions" prop changes or the "showWeekdayBars" state changes.
  useEffect(() => {
    // Destroy the previous chart instance, if any.
    if (chartRef.current) {
      chartRef.current.destroy();
    }

    // Transform the "predictions" data into a format that can be used by the chart.
    const data = predictions.reduce((acc, item) => {
      const date = new Date(item.date).toLocaleDateString('en-US', {
        weekday: 'long',
      });
      const count = acc[date] ? acc[date][item.prediction] + 1 : 1;
      acc[date] = {
        [item.prediction]: count,
        date,
      };
      return acc;
    }, {});

    // Define the chart data and options.
    const chartData = {
      labels: showWeekdayBars ? Object.keys(data) : ['Attacks', 'Benign'],
      datasets: showWeekdayBars
        ? [
            {
              label: 'Attacks',
              data: Object.values(data).map((d) => d['ATTACK'] || 0),
              backgroundColor: 'blue',
            },
            {
              label: 'Benign',
              data: Object.values(data).map((d) => d['BENIGN'] || 0),
              backgroundColor: 'green',
            },
          ]
        : [
            {
              label: 'Attacks',
              data: [Object.values(data).reduce((acc, d) => acc + (d['ATTACK'] || 0), 0)],
              backgroundColor: 'blue',
            },
            {
              label: 'Benign',
              data: [Object.values(data).reduce((acc, d) => acc + (d['BENIGN'] || 0), 0)],
              backgroundColor: 'green',
            },
          ],
    };

    const chartConfig = {
      type: 'bar',
      data: chartData,
      options: {
        maintainAspectRatio: false,
        scales: {
          y: {
            beginAtZero: true,
          },
        },
        onClick: function (event, elements) {
          if (showWeekdayBars) {
            // The user clicked on a weekday bar.
            if (elements.length > 0) {
              const index = elements[0].index;
              const selectedWeekday = Object.keys(data)[index];
              alert(`You clicked on ${selectedWeekday}.`);
            }
          } else {
            // The user clicked on the overall "Attacks" or "Benign" bar.
            setShowWeekdayBars(true);
          }
        },
      },
    };

    // Get the canvas context and create a new chart instance.
    const ctx = document.getElementById('BarChart').getContext('2d');
    if (chartRef.current) {
      chartRef.current.destroy();
    }
    chartRef.current = new Chart(ctx, chartConfig);
  }, [predictions, showWeekdayBars]); // The "predictions" prop and the "showWeekdayBars" state are the dependencies of this useEffect hook.

  return <canvas id="BarChart"></canvas>;
}

export default BarChart;



